import React, { useEffect, useState } from "react";
import { Container, Row, Col } from "reactstrap";
import CommanSection from "../shared/CommanSection";
import "../style/tours.css";
import TourCard from "../shared/TourCard";
import SearchBar from "../shared/SearchBar";
import NewsLetter from "../shared/NewsLetter";
import tourData from "../assets/data/tours.js";

const Tours = () => {
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);

  useEffect(() => {
    const pages = Math.ceil(5 / 8);
    setPageCount(pages);
  }, []);

  return (
    <>
      <CommanSection title={"All Tours"} />
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section className="pt-0">
        <Container>
          <Row>
            {tourData.map((tour) => (
              <Col lg="3" key={tour.id} className="mb-4">
                <TourCard tour={tour} />
              </Col>
            ))}

            <Col lg="12">
              <div className="padination d-flex align-items-center justify-content-center mt-4 gap-3">
                {[...Array(pageCount)]}
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default Tours;
