import React from "react";

import Masonry, { ResponsiveMasonry } from "react-responsive-masonry";
import galleryImg from "./galleryImages";

const MasnoryImageGallery = () => {
  return (
    <>
    <div>

    <ResponsiveMasonry columnsCountBreakPoints={{ 350:1, 768:2, 992:4 }}>
      <Masonry gutter="1rem">
        {galleryImg.map((item, index) => (
          <img
            className="masonary__img"
            src={item}
            key={index}
            alt=""
            style={{
                width: "100%",
                display: "block",
                borderRadius: "10px",
                alignSelf: "flex-end", // Align images to the bottom
            }}
          />
        ))}
      </Masonry>
    </ResponsiveMasonry>
    </div>

    </>
  );
};

export default MasnoryImageGallery;
