import React from "react";
import { Card, CardBody } from "reactstrap";
import {Link} from "react-router-dom"

const TourCard = ({ tour }) => {
  const { id, title, photo, price, featured, avgRating, reviews ,city} = tour;

  return (
    <div className="tour_card">
      <Card>
        <div className="tour_img">
          <img src={photo} alt="tour-img" />
          <span>Featured</span>
        </div>
      </Card>

      <CardBody>
        <div className="card_top d-flex align-items-center justify-align-content-between">
            <span className="tour__location d-flex align-items-center gap-1">
            <i class="ri-map-pin-line"></i>{city}
            </span>
            <span className="tour__rating d-flex align-items-center gap-1">
            <i class="ri-star-fill"></i>{avgRating}{" "}<span>({reviews.length})</span>
            </span>
        </div>
        <h5 className="tour__title">
            <Link to={`/tours/${id}`}/>
        </h5>
        <div className="card__button d-flex align-items-center justify-content-center mt-3">

        </div>
      </CardBody>
    </div>
  );
};

export default TourCard;
