import { useEffect } from "react";
import {useLocation,useParams} from "react-router-dom"
import "./App.css";
import Layout from "./components/layout/layout";



const App = () => {

  const location = useLocation();

  const id=useParams

  useEffect(() => {
    // Function to update the document title based on the current URL
    const updateTitle = () => {
      const pathname = location.pathname;
      let pageTitle = "Default Title"; // Default title

      // Switch statement to set different titles for different URLs
      switch (pathname) {
        case "/home":
          pageTitle = "Home Page";
          break;
        case "/about":
          pageTitle = "About Us";
          break;
        case "/tours":
          pageTitle = "Tours";
          break;
        // Add more cases for other URLs as needed
        default:
          pageTitle = "Default Title";
      }

      // Update the document title
      document.title = pageTitle;
    };

    // Call the function to update the title when the component mounts
    updateTitle();

    // Clean up function to remove the event listener when the component unmounts
    return () => {
      // No cleanup needed in this case
    };
  }, [location.pathname]); // Dependency array ensures that the effect runs when the pathname changes


  return (
    <>
      <Layout/>
    </>
  )
}

export default App
